sysauto_script.script:
// Get today's time and date
	var rightNow = new GlideDateTime();

	// Query the database for NeedIt Task records with Due date field values between
	// now and 24 hours in future.  Only return NeedIt Task records that do not have
	// a State field value of Closed Complete.
	var soondueNITasks = new GlideRecord('x_58872_needit_needit_task');
	soondueNITasks.addQuery('due_date','>=',rightNow);
    soondueNITasks.addQuery('due_date','<',gs.hoursAgo(-24));
	soondueNITasks.addQuery('state','!=',3);

	soondueNITasks.query();
	// Write a log message for each soon due NeedIt Task Record
	while(soondueNITasks.next()){
		gs.info("Soon due NeedIt Task record = " + soondueNITasks.number);
	}
